import React from 'react'
import styled from 'styled-components'
import avatar from '../../img/avatar.png'
import { signout } from '../../utils/Icons'
import { menuItems } from '../../utils/menuItems'
import { useHistory } from 'react-router-dom' // If you are using React Router

function Navigation({ active, setActive, setIsSignedIn }) {
    const history = useHistory(); // Hook to redirect after sign out

    // Function to handle sign out
    const handleSignOut = () => {
        // Clear session or local storage
        localStorage.removeItem('userToken'); // Or use sessionStorage depending on your implementation

        // Update global state (if using context or state management)
        setIsSignedIn(false); // Update the state to indicate the user is logged out

        // Redirect to the login page
        history.push('/login'); // Assuming your login page is at '/login'

        console.log("Sign out clicked!");
    }

    return (
        <NavStyled>
            <div className="user-con">
                <img src={avatar} alt="" />
                <div className="text">
                    <h2>Mike</h2>
                    <p>Your Money</p>
                </div>
            </div>
            <ul className="menu-items">
                {menuItems.map((item) => {
                    return <li
                        key={item.id}
                        onClick={() => setActive(item.id)}
                        className={active === item.id ? 'active' : ''}>
                        {item.icon}
                        <span>{item.title}</span>
                    </li>
                })}
            </ul>
            <div className="bottom-nav">
                <li>
                    <button onClick={handleSignOut} className="signout-btn">
                        {signout} Sign Out
                    </button>
                </li>
            </div>
        </NavStyled>
    )
}

const NavStyled = styled.nav`
    // Your existing styles...
`

export default Navigation;
