import React, { useEffect } from 'react'
import styled from 'styled-components'
import { useGlobalContext } from '../../context/globalContext';
import History from '../../History/History';
import { InnerLayout } from '../../styles/Layouts';
import { rupee } from '../../utils/Icons';
import Chart from '../Chart/Chart';

function Dashboard() {
    const { totalExpenses, incomes, expenses, totalIncome, totalBalance, getIncomes, getExpenses, user } = useGlobalContext()

    useEffect(() => {
        getIncomes()
        getExpenses()
    }, [])

    return (
        <DashboardStyled>
            <InnerLayout>
                <h1>Welcome, {user ? user.username : 'Guest'}</h1> {/* Display username */}
                <div className="stats-con">
                    <div className="chart-con">
                        <Chart />
                        <div className="amount-con">
                            <div className="income">
                                <h2>Total Income</h2>
                                <p>
                                    {rupee} {totalIncome()}
                                </p>
                            </div>
                            <div className="expense">
                                <h2>Total Expense</h2>
                                <p>
                                    {rupee} {totalExpenses()}
                                </p>
                            </div>
                            <div className="balance">
                                <h2>Total Balance</h2>
                                <p>
                                    {rupee} {totalBalance()}
                                </p>
                            </div>
                        </div>
                    </div>
                    <div className="history-con">
                        <History />
                        <h2 className="salary-title">Min <span>Salary</span>Max</h2>
                        <div className="salary-item">
                            <p>
                            ₹{Math.min(...incomes.map(item => item.amount))}
                            </p>
                            <p>
                            ₹{Math.max(...incomes.map(item => item.amount))}
                            </p>
                        </div>
                        <h2 className="salary-title">Min <span>Expense</span>Max</h2>
                        <div className="salary-item">
                            <p>
                            ₹{Math.min(...expenses.map(item => item.amount))}
                            </p>
                            <p>
                            ₹{Math.max(...expenses.map(item => item.amount))}
                            </p>
                        </div>
                    </div>
                </div>
            </InnerLayout>
        </DashboardStyled>
    )
}

const DashboardStyled = styled.div`
    .stats-con {
        display: grid;
        grid-template-columns: repeat(5, 1fr);
        gap: 2rem;

        .chart-con {
            grid-column: 1 / 4;
            height: 400px;

            .amount-con {
                display: grid;
                grid-template-columns: repeat(4, 1fr);
                gap: 2rem;
                margin-top: 2rem;

                .income, .expense {
                    grid-column: span 2;
                }

                .income, .expense, .balance {
                    background: #FFFAF1; /* Soft cream */
                    border: 2px solid #F1F1F1;
                    box-shadow: 0px 4px 20px rgba(0, 0, 0, 0.1);
                    border-radius: 20px;
                    padding: 1.5rem;

                    p {
                        font-size: 3.5rem;
                        font-weight: 700;
                        color: #2D4C8E; /* Deep blue */
                    }
                }

                .balance {
    grid-column: 2 / 4;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: row; /* Align items horizontally */
    gap: 0.5rem; /* Space between symbol and amount */

    p {
        color: #2ECC71; /* Emerald Green */
        font-size: 4.5rem;
        font-weight: 600;
        opacity: 0.8;
        margin: 0; /* Remove any default margin */
    }
}

            }
        }

        .history-con {
            grid-column: 4 / -1;

            h2 {
                margin: 1rem 0;
                display: flex;
                align-items: center;
                justify-content: space-between;
                color: #2D4C8E; /* Deep blue */
            }

            .salary-title {
                font-size: 1.3rem;
                color: #2D4C8E; /* Deep blue */

                span {
                    font-size: 1.8rem;
                    color: #FF7043; /* Warm Coral */
                }
            }

            .salary-item {
                background: #F9F9F9; /* Light gray */
                border: 2px solid #F1F1F1;
                box-shadow: 0px 2px 10px rgba(0, 0, 0, 0.08);
                padding: 1.5rem;
                border-radius: 20px;
                display: flex;
                justify-content: space-between;
                align-items: center;

                p {
                    font-weight: 600;
                    font-size: 1.6rem;
                    color: #333333; /* Dark Gray */
                }
            }
        }
    }
`;

export default Dashboard;
