import React, { useState } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';

function SignupPage({ setIsSignedIn }) {
  const { setError } = useGlobalContext(); // Use the error context
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  
  const handleSignup = async (e) => {
    e.preventDefault();
    
    // Here you can call an API or any logic to handle signup
    if (username && email && password) {
      // Simulate a successful signup process
      setIsSignedIn(true);  // Set the sign-in state to true
    } else {
      setError('Please fill in all fields.');
    }
  };

  return (
    <SignupPageStyled>
      <h2>Sign Up</h2>
      <form onSubmit={handleSignup}>
        <div className="form-group">
          <label htmlFor="username">Username</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter your username"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email</label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter your email"
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter your password"
            required
          />
        </div>

        <button type="submit" className="btn">Sign Up</button>
      </form>
    </SignupPageStyled>
  );
}

const SignupPageStyled = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100%;
  padding: 2rem;
  background: #FCF6F9;
  border-radius: 20px;
  box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);

  h2 {
    color: var(--primary-color);
    margin-bottom: 1rem;
  }

  form {
    display: flex;
    flex-direction: column;
    gap: 1rem;
    width: 100%;
    max-width: 400px;

    .form-group {
      display: flex;
      flex-direction: column;
      gap: 0.5rem;
      
      label {
        color: var(--primary-color);
        font-weight: bold;
      }

      input {
        padding: 0.8rem;
        border-radius: 10px;
        border: 2px solid var(--primary-color);
        font-size: 1rem;
        outline: none;
        transition: border 0.3s;

        &:focus {
          border-color: var(--color-accent);
        }
      }
    }

    .btn {
      padding: 0.8rem;
      background-color: var(--primary-color);
      color: #fff;
      border: none;
      border-radius: 10px;
      font-size: 1.1rem;
      cursor: pointer;
      transition: background-color 0.3s;

      &:hover {
        background-color: var(--color-accent);
      }
    }
  }
`;

export default SignupPage;
