import React, { useContext, useState, useEffect } from "react"
import axios from 'axios'

const BASE_URL = "http://localhost:5000/api/v1/"

const GlobalContext = React.createContext()

export const GlobalProvider = ({ children }) => {

    const [incomes, setIncomes] = useState([])
    const [expenses, setExpenses] = useState([])
    const [error, setError] = useState(null)

    // Fetch incomes
    const getIncomes = async () => {
        try {
            const response = await axios.get(`${BASE_URL}get-incomes`)
            setIncomes(response.data)
        } catch (err) {
            setError(err.response ? err.response.data.message : 'An error occurred')
        }
    }

    // Fetch expenses
    const getExpenses = async () => {
        try {
            const response = await axios.get(`${BASE_URL}get-expenses`)
            setExpenses(response.data)
        } catch (err) {
            setError(err.response ? err.response.data.message : 'An error occurred')
        }
    }

    useEffect(() => {
        getIncomes()  // Get incomes initially
        getExpenses() // Get expenses initially
    }, [])

    // Add an income
    const addIncome = async (income) => {
        try {
            await axios.post(`${BASE_URL}add-income`, income)
            // After adding income, only fetch the incomes
            getIncomes()  // Fetch updated incomes
        } catch (err) {
            setError(err.response ? err.response.data.message : 'An error occurred')
        }
    }

    // Add an expense
    const addExpense = async (expense) => {
        try {
            await axios.post(`${BASE_URL}add-expense`, expense)
            // After adding expense, only fetch the expenses
            getExpenses()  // Fetch updated expenses
        } catch (err) {
            setError(err.response ? err.response.data.message : 'An error occurred')
        }
    }

    // Delete an income
    const deleteIncome = async (id) => {
        try {
            await axios.delete(`${BASE_URL}delete-income/${id}`)
            getIncomes() // Fetch updated incomes
        } catch (err) {
            setError(err.response ? err.response.data.message : 'An error occurred')
        }
    }

    // Delete an expense
    const deleteExpense = async (id) => {
        try {
            await axios.delete(`${BASE_URL}delete-expense/${id}`)
            getExpenses() // Fetch updated expenses
        } catch (err) {
            setError(err.response ? err.response.data.message : 'An error occurred')
        }
    }

    // Calculate total income
    const totalIncome = () => {
        let totalIncome = 0
        incomes.forEach((income) => {
            totalIncome += income.amount
        })
        return totalIncome
    }

    // Calculate total expenses
    const totalExpenses = () => {
        let totalExpenses = 0
        expenses.forEach((expense) => {
            totalExpenses += expense.amount
        })
        return totalExpenses
    }

    // Calculate total balance (income - expenses)
    const totalBalance = () => {
        return totalIncome() - totalExpenses()
    }

    // Get transaction history (latest 3 transactions)
    const transactionHistory = () => {
        const history = [...incomes, ...expenses]
        history.sort((a, b) => {
            return new Date(b.createdAt) - new Date(a.createdAt)
        })

        return history.slice(0, 3)
    }

    return (
        <GlobalContext.Provider value={{
            addIncome,
            getIncomes,
            incomes,
            deleteIncome,
            expenses,
            totalIncome,
            addExpense,
            getExpenses,
            deleteExpense,
            totalExpenses,
            totalBalance,
            transactionHistory,
            error,
            setError
        }}>
            {children}
        </GlobalContext.Provider>
    )
}

export const useGlobalContext = () => {
    return useContext(GlobalContext)
}
