import React, { useState } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';

function SignupPage({ setIsSignedIn }) {
  const { setError, handleSignup } = useGlobalContext(); // Use the context to update user data
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  
  const handleSignupSubmit = (e) => {
    e.preventDefault();
    
    // Check for empty fields
    if (username && email && password) {
      const userData = { username, email, password }; // User data
      handleSignup(userData);  // Save user data in global context
      setIsSignedIn(true);  // Set signed-in status
    } else {
      setError('Please fill in all fields.');
    }
  };

  return (
    <SignupPageStyled>
      <div className="form-container">
        <h2>Sign Up</h2>
        <form onSubmit={handleSignupSubmit}>
          <div className="form-group">
            <label htmlFor="username">Username</label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              placeholder="Enter your username"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="Enter your email"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter your password"
              required
            />
          </div>

          <button type="submit" className="btn">Sign Up</button>
        </form>
      </div>
    </SignupPageStyled>
  );
}

const SignupPageStyled = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100%;
  padding: 2rem;
  background: #FCF6F9;
  border-radius: 20px;
  box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);

  .form-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 1.5rem;
    width: 100%;
    max-width: 400px;

    h2 {
      color: var(--primary-color);
      margin-top: 0;
      text-align: center;
    }

    form {
      display: flex;
      flex-direction: column;
      gap: 1rem;
      width: 100%;

      .form-group {
        display: flex;
        flex-direction: column;
        gap: 0.5rem;
        
        label {
          color: var(--primary-color);
          font-weight: bold;
        }

        input {
          padding: 0.8rem;
          border-radius: 10px;
          border: 2px solid var(--primary-color);
          font-size: 1rem;
          outline: none;
          transition: border 0.3s;

          &:focus {
            border-color: var(--color-accent);
          }
        }
      }

      .btn {
        padding: 0.8rem;
        background-color: var(--primary-color);
        color: #fff;
        border: none;
        border-radius: 10px;
        font-size: 1.1rem;
        cursor: pointer;
        transition: background-color 0.3s;

        &:hover {
          background-color: var(--color-accent);
        }
      }
    }
  }
`;
    
export default SignupPage;
